mapper.select:
SELECT DISTINCT a.contact_mobile AS "contactMobile", a.id AS "id", a.contact_name AS contactName, a.buyer_id AS "buyerId", a.create_time AS "createTime", b.family_relation AS "familyrelation", IFNULL(d.smsNum, 0) AS "smsNum", IFNULL(d.telNum, 0) AS "telNum", c.name AS rcname FROM t_buyer_contact a LEFT JOIN t_risk_contact AS c on a.contact_mobile = c.tel LEFT JOIN t_risk_buyer2contacts AS b ON b.buyer_id = a.buyer_id AND c.id = b.buyer_contact_id LEFT JOIN ( SELECT ContantTarget AS tel, SUM(CASE WHEN ContantType = 'sms' THEN 1 ELSE 0 END) AS smsNum, SUM(CASE WHEN ContantType = 'tel' THEN 1 ELSE 0 END) AS telNum FROM t_mis_contant_record WHERE Dealcode = #{dealcode} GROUP BY ContantTarget ) AS d on d.tel = a.contact_mobile
AND a.buyer_id = #{buyerId}
ORDER BY ${page.orderBy}
