mapper.select:
SELECT

FROM t_mis_agent_info a
WHERE a.id = #{id}

SELECT a.dbid as "dbid", a.id as "id", a.agent as "agent", a.extension as "extension", a.direct as "direct", a.queue as "queue", a.peopleid as "peopleId", a.status as "status", a.createby as "createBy.name", a.createdate as "createdate", a.updateby as "updateBy.name", a.updatedate as "updatedate", p.nickname as "nickName" FROM t_mis_agent_info a left join t_mis_dunning_people p on a.peopleId=p.id
a.agent=#{agent}
p.nickName=#{nickName}
ORDER BY ${page.orderBy}
ORDER BY a.dbid

SELECT 

FROM t_mis_agent_info a		


		ORDER BY ${page.orderBy}

mapper.update:
UPDATE t_mis_agent_info SET

		extension = #{extension},


		direct = #{direct},


		queue = #{queue},


		status = #{status},

	updateby = #{updateBy.name},
	updatedate = #{updateDate}
WHERE


		peopleid = #{peopleId}


		agent = #{agent}

mapper.select:
SELECT
	a.agent
FROM t_mis_agent_info a


		AND a.agent=#{agent}


		AND a.id != #{id}


limit 1

SELECT
	a.agent
FROM t_mis_agent_info a


		AND a.queue=#{queue}


		AND a.id != #{id}


limit 1

SELECT
	a.agent
FROM t_mis_agent_info a


		AND a.extension=#{extension}


		AND a.id != #{id}


limit 1

SELECT
	a.direct
FROM t_mis_agent_info a


		AND a.direct=#{direct}


		AND a.id != #{id}


limit 1
