mapper.select:
SELECT
	
FROM t_mis_calling_record a
WHERE a.id = #{id}

SELECT a.dbid as "dbid", a.id as "id", a.agent as "agent", a.peopleid as "peopleId", p.nickname as "peopleName", a.CallType as "callType", a.ExtensionNumber as "extensionNumber", a.TargetNumber as "targetNumber", a.CustomerNo as "customerNo", a.CallTime as "callTime", a.RingTime as "ringTime", a.StartTime as "startTime", a.EndTime as "endTime", a.FinishTime as "finishTime", a.DurationTime as "durationTime", ( SELECT o.dealcode FROM t_risk_buyer b INNER JOIN t_risk_order o ON b.id = o.buyer_id INNER JOIN t_mis_dunning_task t ON o.dealcode = t.dealcode WHERE b.mobile = a.TargetNumber ORDER BY o.id DESC LIMIT 1 ) as "dealcode", a.Uuid as "uuid", a.createby as "createBy.name", a.createdate as "createDate", a.updateby as "updateBy.name", a.updatedate as "updateDate" FROM t_mis_calling_record a LEFT JOIN t_mis_dunning_people p on a.peopleid = p.id
ORDER BY ${page.orderBy}
ORDER BY a.CallTime DESC
LIMIT ${page.firstResult}, ${page.pageSize}

sql.where:
a.FinishTime IS NOT NULL

	AND a.TargetNumber = #{targetNumber}


	AND p.groupId = #{dunningPeople.group.id}


	AND p.groupId in
	
		('')
	
	
		
			#{groupId}
		
	


	AND a.peopleId in
	
		#{item}
	


	AND a.peopleId = #{peopleId}


	 AND a.CallTime >= #{callTimeFrom} 


	 AND a.CallTime < #{callTimeTo} 


	AND a.callType = #{callType}


	
		
		
			AND a.StartTime IS NOT NULL
		
		
		
			AND a.callType = 'out'
			AND a.StartTime IS NULL
		
		
		
			AND a.callType = 'in'
			AND a.StartTime IS NULL
			AND a.RingTime IS NOT NULL
		
		
		
			AND a.callType = 'in'
			AND a.StartTime IS NULL
			AND a.RingTime IS NULL

