mapper.select:
SELECT 

FROM t_mis_contant_record a

WHERE a.id = #{id}

SELECT a.dbid AS "dbid", a.id AS "id", a.taskid AS "taskid", a.dealcode AS "dealcode", a.orderstatus AS "orderstatus", a.overduedays AS "overduedays", a.remark AS "remark", a.dunningtime AS "dunningtime", a.contanttype AS "contanttype", a.contanttarget AS "contanttarget", a.content AS "content", a.contactstype AS "contactstype", a.smstemp AS "templateName", a.smstype AS "smstype", a.telstatus AS "telstatus", a.field1 AS "field1", a.dunningpeoplename AS "dunningpeoplename", CASE WHEN a.dunningpeoplename = 'sys' THEN '' ELSE p.nickname END AS "peoplename", a.repaymenttime AS "repaymenttime", a.iseffective AS "iseffective", a.promisepaydate AS "promisepaydate", a.contactsname AS "contactsname", a.conclusionid AS "conclusionid", CASE WHEN a.dunningpeoplename = 'sys' THEN 'sys' ELSE a.createby END AS "createBy.name", a.createdate AS "createDate", a.updateby AS "updateBy.name", a.updatedate AS "updateDate" FROM t_mis_contant_record a LEFT JOIN t_mis_dunning_people p ON p.Id = a.dunningpeoplename LEFT JOIN t_risk_order o ON o.dealcode = a.dealcode LEFT JOIN t_mis_dunning_task AS t ON t.dealcode = a.dealcode
WHERE t.dbid IS NOT NULL AND o.buyer_id = #{buyerid}
ORDER BY ${page.orderBy}
ORDER BY a.dbid desc

SELECT 

FROM t_mis_contant_record a






		ORDER BY ${page.orderBy}

mapper.insert:
INSERT INTO t_mis_contant_record ( id, taskid, dealcode, orderstatus, overduedays, remark, dunningtime, contanttype, contanttarget, content, contactstype, telstatus, smstemp, smsType, field1, dunningpeoplename, repaymenttime, iseffective, promisepaydate, contactsname, dunningCycle, createby, createdate, updateby, updatedate
) values
( #{item.id}, #{item.taskid}, #{item.dealcode}, #{item.orderstatus}, #{item.overduedays}, #{item.remark}, #{item.dunningtime}, #{item.contanttype}, #{item.contanttarget}, #{item.content}, #{item.contactstype}, #{item.telstatus}, #{item.templateName}, #{item.smsType}, #{item.field1}, #{item.dunningpeoplename}, #{item.repaymenttime}, #{item.iseffective}, #{item.promisepaydate}, #{item.contactsname}, #{item.dunningCycle}, #{item.createBy.name}, #{item.createDate}, #{item.updateBy.name}, #{item.updateDate}
)

mapper.select:
select
from t_mis_contant_record a where a.dealcode=#{dealcode} and a.dunningpeoplename=#{dunningpeopleId} and a.dunningCycle=#{dunningCycle}
and a.CreateDate >=#{createDate}
and a.ContantType='tel' and a.ContactsType in('SELF','MARRIED','PARENT','CHILDREN','RELATIVES','FRIEND','WORKMATE') order by a.CreateDate

SELECT count(*) FROM `t_mis_contant_record` WHERE Dealcode = #{dealcode} AND SmsTemp=#{templateName}
AND contanttarget=#{contanttarget}
AND ContantType='sms' AND IFNULL(Remark,'') !='短信发送失败' AND CreateDate BETWEEN DATE_FORMAT(CURDATE(),'%Y-%m-%d 00:00:00') AND now() ;
