mapper.select:
SELECT 
	
FROM t_mis_dunned_conclusion a
WHERE a.id = #{id}

SELECT a.dbid AS "dbid", a.id AS "id", a.taskid AS "taskid", a.dealcode AS "dealcode", a.orderstatus AS "orderstatus", a.repaymenttime AS "repaymenttime", a.iseffective AS "iseffective", a.overduereason AS "overduereason", a.resultcode AS "resultcode", a.promisepaydate AS "promisepaydate", CASE WHEN a.dunningpeopleid = t.dunningpeopleid AND a.dunningcycle = t.dunningcycle THEN a.nextfollowdate ELSE NULL END AS "nextfollowdate", a.remark AS "remark", a.dunningtime AS "dunningtime", a.dunningpeopleid AS "dunningpeopleid", CASE WHEN p.Name = 'sys' THEN '' ELSE p.Name END AS "dunningpeoplename", a.createby AS "createBy.name", a.createdate AS "createDate", a.updateby AS "updateBy.name", a.updatedate AS "updateDate" FROM t_mis_dunned_conclusion a LEFT JOIN t_risk_order o ON o.dealcode = a.dealcode LEFT JOIN t_mis_dunning_people p ON p.Id = a.dunningpeopleid LEFT JOIN t_mis_dunning_task t ON a.dealcode = t.dealcode WHERE t.dbid IS NOT NULL AND o.buyer_id = #{buyerid}
ORDER BY ${page.orderBy}
ORDER BY a.dbid desc

SELECT 
	
FROM t_mis_dunned_conclusion a		

	
		ORDER BY ${page.orderBy}

