mapper.select:
SELECT
	
FROM t_mis_dunning_deduct a
WHERE a.deductcode = #{deductcode}

SELECT a.dbid AS "dbid", a.id AS "id", a.dealcode AS "dealcode", a.deductcode AS "deductcode", a.starttime AS "starttime", a.buyername AS "buyername", a.idcard AS "idcard", a.bankname AS "bankname", a.bankcard AS "bankcard", a.mobile AS "mobile", a.paytype AS "paytype", a.delaydays AS "delaydays", a.payamount AS "payamount", IFNULL(d.label, ' ') AS "paychannel", a.status AS "status", a.statusdetail AS "statusdetail", a.reason as "reason", a.repaymentstatus AS "repaymentstatus", a.repaymentdetail AS "repaymentdetail", a.finishtime AS "finishtime", a.createby AS "createBy.name", a.createdate AS "createDate", a.updateby AS "updateBy.name", a.updatedate AS "updateDate" FROM t_mis_dunning_deduct a LEFT JOIN sys_dict d ON d.type = 'pay_channel' AND a.paychannel = d.value and d.del_flag = '0' WHERE a.dealcode = #{dealcode}
ORDER BY ${page.orderBy}
ORDER BY a.dbid desc

SELECT 
	
FROM t_mis_dunning_deduct a		

	
		ORDER BY ${page.orderBy}

SELECT d.value as "channelid", d.label as "channelname", t.successrate as "successrate" FROM sys_dict d LEFT JOIN ( SELECT a.paychannel, COUNT(CASE a.paychannel WHEN 'yilianpay' THEN CASE a.reason WHEN 'NO_BALANCE' THEN NULL ELSE 1 END ELSE CASE a.status WHEN 'succeeded' THEN 1 ELSE NULL END END ) / COUNT(1) * 100 as successrate FROM t_mis_dunning_deduct a
a.paychannel = #{paychannel}
GROUP BY a.paychannel ) t ON d.value = t.paychannel WHERE d.type = 'pay_channel' AND d.del_flag = '0' ORDER BY d.sort

SELECT 
	
FROM t_mis_dunning_deduct a
WHERE a.status = #{status}

SELECT t.dealcode FROM t_mis_dunning_task t LEFT JOIN t_risk_order o ON t.dealcode = o.dealcode WHERE o.type = 'loan' AND o.merchant_id = 7 AND o.status = 'payment'
AND DATEDIFF(CURDATE(), o.repayment_time) = #{overduedays}
AND t.dunningpeopleid in
#{item}
