mapper.select:
SELECT 
	
FROM t_mis_dunning_deduct_log a
WHERE a.id = #{id}

SELECT a.dbid AS "dbid", a.id AS "id", a.dealcode AS "dealcode", a.deductcode AS "deductcode", a.buyername AS "buyername", a.idcard AS "idcard", a.bankname AS "bankname", a.bankcard AS "bankcard", a.mobile AS "mobile", a.paytype AS "paytype", a.delaydays AS "delaydays", a.payamount AS "payamount", a.paychannel AS "paychannel", a.status AS "status", a.statusdetail AS "statusdetail", a.reason AS "reason", a.repaymentstatus AS "repaymentstatus", a.repaymentdetail AS "repaymentdetail", a.createby AS "createBy.name", a.createdate AS "createDate", a.updateby AS "updateBy.name", a.updatedate AS "updateDate" FROM t_mis_dunning_deduct_log a WHERE a.dealcode = #{dealcode}
ORDER BY ${page.orderBy}
ORDER BY a.dbid desc

