mapper.select:
SELECT o.dealcode AS "dealcode", o.buyer_id AS "buyer_id", b.mobile AS "mobile", idc.real_name AS "realname" FROM t_risk_order AS o LEFT JOIN t_risk_buyer AS b ON o.buyer_id = b.id LEFT JOIN t_risk_buyer_idcard AS idc ON idc.buyer_id = o.buyer_id WHERE o.merchant_id = 7 AND o.`status`= 'payment' AND o.type = 'loan'
AND b.mobile = #{mobile}
AND o.dealcode = #{dealcode}

SELECT idc.real_name AS "realname", o.dealcode AS "dealcode", o.buyer_id AS "buyer_id", b.mobile AS "mobile", o.amount AS "amount", AS remainAmmount, DATEDIFF(CURDATE(), o.repayment_time) AS overduedays, p.`nickname` AS `dunningPeople.nickname` FROM t_risk_order AS o LEFT JOIN t_risk_buyer AS b ON o.buyer_id = b.id LEFT JOIN t_risk_buyer_idcard AS idc ON idc.buyer_id = o.buyer_id LEFT JOIN t_mis_dunning_task AS t ON t.dealcode = o.dealcode LEFT JOIN t_mis_dunning_people AS p ON t.DunningPeopleId = p.id WHERE o.merchant_id = 7 AND o.`status`= 'payment' AND o.type = 'loan'
AND b.mobile = #{mobile}
AND o.dealcode = #{dealcode}
