		name = #{name},
	
	
		supervisorId = #{supervisor.id},
	
	updateby = #{updateBy.id},
	updatedate = #{updateDate}
WHERE id = #{id}

UPDATE t_mis_dunning_organization SET 	
	updateby = #{updateBy.name},
	updatedate = #{updateDate},
	delFlag = #{delFlag}
WHERE id = #{id}
