mapper.select:
SELECT 

FROM t_mis_dunning_task_log a

WHERE a.dbid = #{dbid}

SELECT
FROM t_mis_dunning_task_log a
AND a.dealcode LIKE '%'||#{dealcode}||'%' '%'+#{dealcode}+'%' concat('%',#{dealcode},'%')
AND a.behaviorstatus = #{behaviorstatus}
AND a.dealcodestatus = #{dealcodestatus}
AND a.payofftime = #{payofftime}
AND a.realname LIKE '%'||#{realname}||'%' '%'+#{realname}+'%' concat('%',#{realname},'%')
AND a.mobile LIKE '%'||#{mobile}||'%' '%'+#{mobile}+'%' concat('%',#{mobile},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM t_mis_dunning_task_log a






		ORDER BY ${page.orderBy}

