mapper.select:
SELECT

FROM t_mis_dunning_task_issue a
WHERE a.id = #{id}

SELECT

FROM t_mis_dunning_task_issue a


        AND a.dealcode = #{dealcode}


        AND a.issueChannel = #{issueChannel}


        AND a.status = #{status}




        ORDER BY ${page.orderBy}

SELECT , (r.taskIssueId IS NOT NULL OR anyr.taskIssueId IS NOT NULL) AS "read" FROM t_mis_dunning_task_issue a LEFT JOIN t_mis_dunning_task t ON a.dealcode = t.dealcode LEFT JOIN t_mis_dunning_people p ON t.DunningPeopleId=p.id -- 已完成案件任意一人读过即算已读, 未完成案件需要本人读 LEFT JOIN t_mis_task_issue_read_record r ON a.status = "UNRESOLVED" AND a.id =r.taskIssueId AND r.userId = #{currentUserId} LEFT JOIN t_mis_task_issue_read_record anyr ON a.status = "RESOLVED" AND a.id =anyr.taskIssueId
AND ( a.userName LIKE concat('%',#{keyWord},'%') OR a.dealcode LIKE concat(#{keyWord},'%') OR a.description LIKE concat('%',#{keyWord},'%') OR p.Nickname LIKE concat('%',#{keyWord},'%') OR a.recorderName LIKE concat('%',#{keyWord},'%') OR p.name LIKE concat('%',#{keyWord},'%') )
AND a.issueTypes LIKE concat('%',#{issueType},'%')
AND a.status = #{status}
AND a.createDate BETWEEN #{beginCreateDate} AND #{endCreateDate}
AND a.updateBy LIKE concat ('%',#{updateBy.name},'%')
AND p.groupId in
('')
#{groupId}
AND t.DunningPeopleId = #{dunningpeopleid}
ORDER BY ${page.orderBy}
